print(sum(max(map(int,[b[i]+b[j]+b[k]+b[l]+b[m]+b[n]+b[o]+b[p]+b[q]+b[r]+b[s]+b[t]for i in range(len(b)-11)for j in range(i+1,len(b)-10)for k in range(j+1,len(b)-9)for l in range(k+1,len(b)-8)for m in range(l+1,len(b)-7)for n in range(m+1,len(b)-6)for o in range(n+1,len(b)-5)for p in range(o+1,len(b)-4)for q in range(p+1,len(b)-3)for r in range(q+1,len(b)-2)for s in range(r+1,len(b)-1)for t in range(s+1,len(b))]))for b in open(0).read().splitlines()))